import React from "react";

// reactstrap components
import {
    Button,
    ButtonGroup,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    Row,
    Col,
    UncontrolledTooltip, Pagination, PaginationItem, PaginationLink
} from "reactstrap";

class NumberList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }

    valueCheck = ()=>{
        for(var i = 1; i<=100; i++){
            return(
                <Button className="btn-round" color="info" type="button">
                    {i/3 === 0 ? "Fizz" : i/5 === 0 ? "Buzz" : i}
                </Button>
            )
        }
    };

    render() {
        return (
            <>
                <div className="content">
                    <Card>
                        <Row>
                            <Col md="6">
                                <CardHeader>
                                    <CardTitle tag="h4">Button Group</CardTitle>
                                </CardHeader>
                                <CardBody>
                                    <ButtonGroup data-toggle="buttons">
                                        {this.valueCheck}
                                    </ButtonGroup>
                                </CardBody>
                            </Col>
                        </Row>
                    </Card>
                </div>
            </>
        );
    }
}

export default NumberList;
